INSERT INTO `blogs` VALUES (25, 'Việt Nam trong top điểm đến \'vừa làm vừa chơi\'', 'Với chi phí sinh hoạt không quá cao, thức ăn ngon và cảnh đẹp, Việt Nam phù hợp cho những người vừa đi du lịch, vừa làm việc từ xa.', '<p class=\"Normal\" style=\"margin-bottom: 1em; padding: 0px; text-rendering: optimizespeed; line-height: 28.8px;\"><font face=\"Arial\">Dân du mục kỹ thuật số (<a href=\"https://vnexpress.net/7-cong-viec-vua-lam-vua-co-the-di-du-lich-4362080.html\" rel=\"dofollow\" style=\"margin: 0px; padding: 0px 0px 1px; text-rendering: optimizelegibility; position: relative; text-underline-position: under; border-bottom-width: 1px; border-bottom-style: solid;\">digital nomads</a>) là những người làm việc khi đi du lịch. Đây là lối sống nhiều người trẻ theo đuổi khi có thể hoàn thành công việc ở bất kỳ đâu miễn là có điện, Internet và máy tính.&nbsp;<span style=\"text-rendering: optimizelegibility;\">The Travel</span>, tạp chí du lịch của Canada, kể tên 10 điểm đến phù hợp cho dân du mục kỹ thuật số, xét trên các yếu tố như hòa bình, chi phí sinh hoạt, con người, công nghệ, điều kiện sống và giải trí.</font></p><figure data-size=\"true\" itemprop=\"associatedMedia image\" itemscope=\"\" itemtype=\"http://schema.org/ImageObject\" class=\"tplCaption action_thumb_added\" style=\"margin-right: auto; margin-bottom: 15px; margin-left: auto; padding: 0px; text-rendering: optimizelegibility; max-width: 100%; clear: both; position: relative; text-align: center; width: 670px; float: left;\"><div class=\"fig-picture\" style=\"margin: 0px; padding: 0px 0px 502.675px; text-rendering: optimizelegibility; width: 670px; float: left; display: table; justify-content: center; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; position: relative;\"><picture style=\"margin: 0px; padding: 0px; text-rendering: optimizelegibility;\"><f